private void attachNullToParent(Node<E> toDelete) {
  Node<E> parent = toDelete.parent;
  if (parent == null) {
    root = null;
  } else {
    if (toDelete == parent.left) {
      parent.left = null;
    } else {
      parent.right = null;
    }
    if (toDelete.isBlack()) {
      balanceDelete(parent);
    }
  }
}
