public static <K, E extends Comparable<K>> int binarySearch(
    final ReadOnlyList<E> list, final K key) {
  int lower = 0;
  for(int upper = list.size() - 1; lower <= upper; ) {
    final int mid = (upper + lower) >>> 1;

    final int d = list.get(mid).compareTo(key);
    if (d == 0) {
      return mid;
    } else if (d > 0) {
      upper = mid - 1;
    } else {
      lower = mid + 1;
    }
  }
  return -(lower + 1);
}

