private void enforceRootPath(HttpFSFileSystem.Operation op, String path) {
  if (!path.equals("/")) {
    throw new UnsupportedOperationException(
      MessageFormat.format("Operation [{0}], invalid path [{1}], must be '/'",
                           op, path));
  }
}
