       String startAfter = params.get(
           HttpFSParametersProvider.StartAfterParam.NAME,
           HttpFSParametersProvider.StartAfterParam.class);
       byte[] token = HttpFSUtils.EMPTY_BYTES;
       if (startAfter != null) {
         token = startAfter.getBytes(Charsets.UTF_8);
       }
