       FSOperations.FSGetAllStoragePolicies command =
           new FSOperations.FSGetAllStoragePolicies();
       JSONObject json = fsExecute(user, command);
       AUDIT_LOG.info("[{}]", path);
       response = Response.ok(json).type(MediaType.APPLICATION_JSON).build();
       break;
