private Param<?> newParam(Class<Param<?>> paramClass) {
  try {
    return paramClass.newInstance();
  } catch (Exception ex) {
    throw new UnsupportedOperationException(
      MessageFormat.format(
        "Param class [{0}] does not have default constructor",
        paramClass.getName()));
  }
}
