static InputStream getResource(String name) {
  Check.notEmpty(name, "name");
  ClassLoader cl = Thread.currentThread().getContextClassLoader();
  if (cl == null) {
    cl = Server.class.getClassLoader();
  }
  return cl.getResourceAsStream(name);
}
