private void verifyDir(String dir) throws ServerException {
  File file = new File(dir);
  if (!file.exists()) {
    throw new ServerException(ServerException.ERROR.S01, dir);
  }
  if (!file.isDirectory()) {
    throw new ServerException(ServerException.ERROR.S02, dir);
  }
}
