void addCron(Cron cron) {
  cron.end();
  lock.lock();
  try {
    last = (last + 1) % size;
    full = full || last == (size - 1);
    total[last] = cron.total;
    own[last] = cron.own;
  } finally {
    lock.unlock();
  }
}
