synchronized boolean purgeIfIdle() throws IOException {
  boolean ret = false;
  if (count == 0 && lastUse != -1 &&
      (System.currentTimeMillis() - lastUse) > timeout) {
    fs.close();
    fs = null;
    lastUse = -1;
    ret = true;
  }
  return ret;
}
