  try {
    long limit = Time.now() + 30 * 1000;
    scheduler.shutdownNow();
    while (!scheduler.awaitTermination(1000, TimeUnit.MILLISECONDS)) {
      LOG.debug("Waiting for scheduler to shutdown");
