public boolean satisfy(final short replication,
    final DatanodeInfo[] existings, final int n, final boolean isAppend,
    final boolean isHflushed) {
  if (replication < 3) {
    return false;
  } else {
    if (n <= (replication/2)) {
      return true;
    } else {
      return isAppend || isHflushed;
    }
  }
}
