    entries[1] = new READDIR3Response.Entry3(dotdotFileId, "..", dotdotFileId);

    for (int i = 2; i < n + 2; i++) {
      entries[i] = new READDIR3Response.Entry3(fstatus[i - 2].getFileId(),
