  // Set up the dirents in the response
  HdfsFileStatus[] fstatus = dlisting.getPartialListing();
  int n = (int) Math.min(fstatus.length, dirCount-2);
  boolean eof = (n < fstatus.length) ? false : (dlisting
      .getRemainingEntries() == 0);

