  }

  StringBuilder str = new StringBuilder();
  str.append("[NsQuota: ").append(nsQuota).append("/")
      .append(nsCount)
      .append(", SsQuota: ").append(ssQuota)
      .append("/").append(ssCount)
      .append("]");
  return str.toString();
}

