static <T> T[] merge(
    Map<FederationNamespaceInfo, T[]> map, Class<T> clazz) {

  // Put all results into a set to avoid repeats
  Set<T> ret = new LinkedHashSet<>();
  for (T[] values : map.values()) {
    if (values != null) {
      for (T val : values) {
        ret.add(val);
      }
    }
  }

  return toArray(ret, clazz);
}

