private static String getHostName(Configuration config)
    throws UnknownHostException {
  String name = config.get(DFS_ROUTER_KERBEROS_PRINCIPAL_HOSTNAME_KEY);
  if (name == null) {
    name = InetAddress.getLocalHost().getHostName();
  }
  return name;
}
