protected void serviceStop() throws Exception {
  LOG.info("Stopping NamenodeHeartbeat service for, NS {} NN {} ",
      this.nameserviceId, this.namenodeId);
  if (this.connectionFactory != null) {
    this.connectionFactory.destroy();
  }
  super.serviceStop();
}
