private <T extends RecordStore<?>> T getStoreInterface(Class<T> clazz)
    throws IOException{
  T store = this.stateStore.getRegisteredRecordStore(clazz);
  if (store == null) {
    throw new IOException("State Store does not have an interface for " +
        clazz.getSimpleName());
  }
  return store;
}
