public boolean validate() {
  boolean ret = super.validate();
  if ((getAddress() == null || getAddress().length() == 0) &&
      getStatus() != RouterServiceState.INITIALIZING) {
    LOG.error("Invalid router entry, no address specified {}", this);
    ret = false;
  }
  return ret;
}
