protected List<String> getChildren(String path) {
  List<String> ret = new LinkedList<>();
  File dir = new File(path);
  File[] files = dir.listFiles();
  if (files != null) {
    for (File file : files) {
      String filename = file.getName();
      ret.add(filename);
    }
  }
  return ret;
}
