public boolean isDriverReady() {
  if (zkManager == null) {
    return false;
  }
  CuratorFramework curator = zkManager.getCurator();
  if (curator == null) {
    return false;
  }
  return curator.getState() == CuratorFrameworkState.STARTED;
}

