public synchronized void submit(BalanceJob job) throws IOException {
  if (!running.get()) {
    throw new IOException("Scheduler is shutdown.");
  }
  String jobId = allocateJobId();
  job.setId(jobId);
  job.setScheduler(this);
  journal.saveJob(job);
  jobSet.put(job, job);
  runningQueue.add(job);
  LOG.info("Add new job={}", job);
}
