public void waitUntilDone(BalanceJob job) {
  BalanceJob found = findJob(job);
  if (found == null || found.isJobDone()) {
    return;
  }
  while (!found.isJobDone()) {
    try {
      found.waitJobDone();
    } catch (InterruptedException e) {
    }
  }
}
