public void recoverJob(BalanceJob job) throws IOException {
  FSDataInputStream in = null;
  try {
    Path logPath = getLatestStateJobPath(job);
    FileSystem fs = FileSystem.get(workUri, conf);
    in = fs.open(logPath);
    job.readFields(in);
    LOG.debug("Recover job={} from journal.", job);
  } finally {
    if (in != null) {
      in.close();
    }
  }
}
