public void clear(BalanceJob job) throws IOException {
  Path jobBase = getJobBaseDir(job);
  FileSystem fs = FileSystem.get(workUri, conf);
  if (fs.exists(jobBase)) {
    fs.delete(jobBase, true);
  }
  LOG.debug("Clear journal of job=" + job);
}
