public void setConf(Configuration conf) {
  Configuration jobConf = null;
  // Force loading of mapred configuration.
  if (conf != null) {
    jobConf = new JobConf(conf);
  } else {
    jobConf = new JobConf();
  }
  super.setConf(jobConf);
}
