public int getJobPriorityAsInteger() {
  String priority = get(JobContext.PRIORITY);
  if (priority == null) {
    return 0;
  }

  int jobPriority = 0;
  try {
    jobPriority = convertPriorityToInteger(priority);
  } catch (IllegalArgumentException e) {
    return Integer.parseInt(priority);
  }
  return jobPriority;
}
