private int convertPriorityToInteger(String priority) {
  JobPriority jobPriority = JobPriority.valueOf(priority);
  switch (jobPriority) {
  case VERY_HIGH :
    return 5;
  case HIGH :
    return 4;
  case NORMAL :
    return 3;
  case LOW :
    return 2;
  case VERY_LOW :
    return 1;
  case DEFAULT :
    return 0;
  default:
    break;
  }

  // If a user sets the priority as "UNDEFINED_PRIORITY", we can return
  // 0 which is also default value.
  return 0;
}
