    isCleanup = Boolean.valueOf(propValue);
  }

  if (taskId == null) {
    taskId = System.getProperty(TASKID_PROPERTY);
  }

  if (maxEvents == null) {
    String propValue = System.getProperty(LOGSIZE_PROPERTY, "0");
    setTotalLogFileSize(Long.valueOf(propValue));
  }
}
