FileStatus getFileStatus(Map<URI, FileStatus> statCache,
    Configuration job, Path p) throws IOException {
  URI u = p.toUri();
  FileStatus status = statCache.get(u);
  if (status == null) {
    status = p.getFileSystem(job).getFileStatus(p);
    statCache.put(u, status);
  }
  return status;
}
