private int convertPriorityToInteger(JobPriority jobPriority) {
  switch (jobPriority) {
  case VERY_HIGH :
    return 5;
  case HIGH :
    return 4;
  case NORMAL :
    return 3;
  case LOW :
    return 2;
  case VERY_LOW :
    return 1;
  case DEFAULT :
    return 0;
  default:
    break;
  }
  // For UNDEFINED_PRIORITY, we can set it to default for better handling
  return 0;
}
