  HdfsPathHandleProto.Builder b = HdfsPathHandleProto.newBuilder();
  b.setPath(path);
  if (inodeId != null) {
    b.setInodeId(inodeId);
  }
  if (mtime != null) {
    b.setMtime(mtime);
  }
  return b.build().toByteString().asReadOnlyByteBuffer();
}

