public boolean equals(Object other) {
  if (null == other) {
    return false;
  }
  if (!HdfsPathHandle.class.equals(other.getClass())) {
    // require exact match
    return false;
  }
  HdfsPathHandle o = (HdfsPathHandle)other;
  return getInodeId() == o.getInodeId();
}
