int getDatanodeWriteTimeout(int numNodes) {
  int confTime =
      conf.getInt(DFSConfigKeys.DFS_DATANODE_SOCKET_WRITE_TIMEOUT_KEY,
                  HdfsConstants.WRITE_TIMEOUT);

  return (confTime > 0) ?
    (confTime + HdfsConstants.WRITE_TIMEOUT_EXTENSION * numNodes) : 0;
}
