public synchronized JobInfo parse() throws IOException {

  if (info != null) {
    return info;
  }

  EventReader reader = new EventReader(in);

  HistoryEvent event;
  info = new JobInfo();
  try {
    while ((event = reader.getNextEvent()) != null) {
      handleEvent(event);
    }
  } finally {
    in.close();
  }
  return info;
}
