    System.err.printf("\t[-list-blacklisted-trackers]%n");
    System.err.println("\t[-list-attempt-ids <job-id> <task-type> " +
      "<task-state>]. " +
      "Valid values for <task-type> are " + getTaskTypes() + ". " +
      "Valid values for <task-state> are " + taskStates);
