  StringBuffer url = new StringBuffer(host.getBaseUrl());

  boolean first = true;
  for (TaskAttemptID mapId : maps) {
    if (!first) {
      url.append(",");
    }
    url.append(mapId);
    first = false;
  }

  LOG.debug("MapOutput URL for " + host + " -> " + url.toString());
  return new URL(url.toString());
}
