public synchronized void hostFailed(String hostname) {
  if (hostFailures.containsKey(hostname)) {
    IntWritable x = hostFailures.get(hostname);
    x.set(x.get() + 1);
  } else {
    hostFailures.put(hostname, new IntWritable(1));
  }
}
