synchronized private List<ControlledJob> getJobsIn(State state) {
  LinkedList<ControlledJob> l = new LinkedList<ControlledJob>();
  for(ControlledJob j: jobsInProgress) {
    if(j.getJobState() == state) {
      l.add(j);
    }
  }
  return l;
}
