public synchronized void waitUntilMoved() {
  while (isMovePending() && !didMoveFail()) {
    try {
      wait();
    } catch (InterruptedException e) {
      LOG.warn("Waiting has been interrupted");
      throw new RuntimeException(e);
    }
  }
}
