public static List<String> convert(String[] targetIds, int idx) {
  List<String> ids = new ArrayList<>();
  if (targetIds != null) {
    for (; idx < targetIds.length; ++idx) {
      ids.add(targetIds[idx]);
    }
  }
  return ids;
}
