private byte[] readFile(Path file, long numBytes) throws IOException {
  byte[] data = new byte[(int)numBytes];
  FSDataInputStream in = fs.open(file);
  try {
    in.readFully(data);
  } finally {
    IOUtils.cleanup(LOG, in);
  }
  return data;
}
