private void deleteFile(Path file) throws IOException {
  boolean deleted;
  try {
    deleted = fs.delete(file, false);
  } catch (FileNotFoundException e) {
    deleted = true;
  }
  if (!deleted) {
    throw new IOException("Unable to delete " + file);
  }
}
