protected void setActiveNavColumnForTask() {
  String tid = $(TASK_ID);
  String activeNav = "2";
  if((tid == null || tid.isEmpty())) {
    activeNav = "1";
  }
  set(initID(ACCORDION, "nav"), "{autoHeight:false, active:"+activeNav+"}");
}
