public static List<Integer> convertBlockIndices(byte[] blockIndices) {
  List<Integer> results = new ArrayList<>(blockIndices.length);
  for (byte bt : blockIndices) {
    results.add(Integer.valueOf(bt));
  }
  return results;
}
