  //TODO these bytes should be versioned
  DataOutputBuffer jobToken_dob = new DataOutputBuffer();
  jobToken.write(jobToken_dob);
  return ByteBuffer.wrap(jobToken_dob.getData(), 0, jobToken_dob.getLength());
}
