private String getErrorMessage(Throwable t) {
  StringBuffer sb = new StringBuffer(t.getMessage());
  while (t.getCause() != null) {
    sb.append(t.getCause().getMessage());
    t = t.getCause();
  }
  return sb.toString();
}
