public void close() throws IOException {
  if (rm != null) {
    rm.close();
  }

  if (historyServerProxy != null) {
    RPC.stopProxy(historyServerProxy);
  }

  if (realProxy != null) {
    RPC.stopProxy(realProxy);
    realProxy = null;
  }
}
