public void close() throws IOException {
  if (rm != null) {
    rm.close();
  }

  if (hsProxy != null) {
    RPC.stopProxy(hsProxy);
    hsProxy = null;
  }

  if (cache != null && !cache.isEmpty()) {
    for (ClientServiceDelegate delegate : cache.values()) {
      if (delegate != null) {
        delegate.close();
        delegate = null;
      }
    }
    cache.clear();
    cache = null;
  }
}
